    18‚Üíexport function AnimatedCommitList({ commits }: AnimatedCommitListProps) {
    19‚Üí  const [visibleCount, setVisibleCount] = useState(10); // Start with 10 visible
    20‚Üí  const [isExpanded, setIsExpanded] = useState(false);
