#!/usr/bin/env node

/**
 * Telegram Notification Tool for Emma's Proposals
 *
 * Sends proposal notifications to Telegram with:
 * - Proposal text
 * - Questions (one message per question)
 * - Job link for tracking
 * - Metadata (platform, budget, confidence)
 *
 * Environment variables required:
 * - TELEGRAM_BOT_TOKEN
 * - TELEGRAM_CHAT_ID
 *
 * Usage:
 *   node telegram-notify.js --proposal /path/to/proposal.json
 */

const fs = require('fs');
const https = require('https');

// Parse command line arguments
const args = process.argv.slice(2);
const proposalIndex = args.indexOf('--proposal');

if (proposalIndex === -1 || !args[proposalIndex + 1]) {
  console.error('Error: --proposal argument required');
  console.error('Usage: node telegram-notify.js --proposal /path/to/proposal.json');
  process.exit(1);
}

const proposalPath = args[proposalIndex + 1];

// Check environment variables
const BOT_TOKEN = process.env.TELEGRAM_BOT_TOKEN;
const CHAT_ID = process.env.TELEGRAM_CHAT_ID;

if (!BOT_TOKEN || !CHAT_ID) {
  console.error('Error: Missing environment variables');
  console.error('Required: TELEGRAM_BOT_TOKEN, TELEGRAM_CHAT_ID');
  process.exit(1);
}

// Read proposal JSON
let proposal;
try {
  const content = fs.readFileSync(proposalPath, 'utf8');
  proposal = JSON.parse(content);
} catch (err) {
  console.error(`Error reading proposal file: ${err.message}`);
  process.exit(1);
}

// Validate proposal structure
const required = ['job_title', 'job_url', 'proposal_text', 'platform', 'decision', 'confidence'];
for (const field of required) {
  if (!proposal[field]) {
    console.error(`Error: Missing required field "${field}" in proposal JSON`);
    process.exit(1);
  }
}

/**
 * Send message to Telegram
 */
function sendTelegramMessage(text, parse_mode = 'Markdown') {
  return new Promise((resolve, reject) => {
    const payload = JSON.stringify({
      chat_id: CHAT_ID,
      text: text,
      parse_mode: parse_mode,
      disable_web_page_preview: false
    });

    const options = {
      hostname: 'api.telegram.org',
      path: `/bot${BOT_TOKEN}/sendMessage`,
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Content-Length': Buffer.byteLength(payload)
      }
    };

    const req = https.request(options, (res) => {
      let data = '';

      res.on('data', (chunk) => {
        data += chunk;
      });

      res.on('end', () => {
        if (res.statusCode === 200) {
          resolve(JSON.parse(data));
        } else {
          reject(new Error(`Telegram API error: ${res.statusCode} - ${data}`));
        }
      });
    });

    req.on('error', (err) => {
      reject(err);
    });

    req.write(payload);
    req.end();
  });
}

/**
 * Format client info for display
 */
function formatClientInfo(client) {
  if (!client) return 'N/A';

  const parts = [];
  if (client.spent) parts.push(`$${client.spent.toLocaleString()} spent`);
  if (client.rating) parts.push(`${client.rating}‚òÖ`);
  if (client.hires) parts.push(`${client.hires} hires`);
  if (client.payment_verified) parts.push('‚úÖ verified');
  if (client.country) parts.push(client.country);

  return parts.join(' ‚Ä¢ ');
}

/**
 * Main execution
 */
async function main() {
  try {
    console.log(`Sending proposal notification for: ${proposal.job_title}`);

    // 1. Send main proposal message with metadata
    const decisionEmoji = proposal.decision === 'STRONG GO' ? 'üéØ' : '‚ö†Ô∏è';
    const mainMessage = `${decisionEmoji} **${proposal.decision}** ‚Ä¢ ${proposal.platform.toUpperCase()} ‚Ä¢ ${proposal.confidence}% confidence

**${proposal.job_title}**

${proposal.budget_range ? `üí∞ ${proposal.budget_range}` : ''}${proposal.bid_amount ? ` (bid: $${proposal.bid_amount})` : ''}
${proposal.client_info ? `üë§ ${formatClientInfo(proposal.client_info)}` : ''}
${proposal.portfolio_match ? `üìÅ Proof: ${proposal.portfolio_match}` : ''}
${proposal.client_type ? `üé≠ Type: ${proposal.client_type}` : ''}
${proposal.urgency ? `‚ö° Urgency: ${proposal.urgency}/10` : ''}
${proposal.pain ? `üò∞ Pain: ${proposal.pain}/10` : ''}

**PROPOSAL:**

${proposal.proposal_text}

üîó ${proposal.job_url}`;

    await sendTelegramMessage(mainMessage);
    console.log('‚úÖ Main proposal sent');

    // Small delay between messages
    await new Promise(resolve => setTimeout(resolve, 500));

    // 2. Send questions (one message per question)
    if (proposal.questions && proposal.questions.length > 0) {
      const questionsHeader = `‚ùì **Questions for Client** (${proposal.questions.length}):`;
      await sendTelegramMessage(questionsHeader);
      console.log('‚úÖ Questions header sent');

      for (let i = 0; i < proposal.questions.length; i++) {
        await new Promise(resolve => setTimeout(resolve, 300));
        const questionMsg = `**Q${i + 1}:** ${proposal.questions[i]}`;
        await sendTelegramMessage(questionMsg);
        console.log(`‚úÖ Question ${i + 1} sent`);
      }
    }

    console.log('\n‚ú® All notifications sent successfully!');
    console.log(`üì± Check Telegram for proposal: ${proposal.job_title}`);

  } catch (err) {
    console.error(`\n‚ùå Error sending notifications: ${err.message}`);
    process.exit(1);
  }
}

main();

