input.content:
.missionFlow {
  max-width: 900px;
  margin: 0 auto;
  padding: 3rem 1.5rem;
  color: var(--text);
}

/* Header */
.header {
  margin-bottom: 3rem;
}

.backLink {
  display: inline-block;
  color: var(--accent);
  text-decoration: none;
  margin-bottom: 1rem;
  font-size: 0.95rem;
  transition: opacity 0.2s;
}

.backLink:hover {
  opacity: 0.7;
}

.header h1 {
  font-size: 2.5rem;
  font-weight: 700;
  margin-bottom: 1rem;
  line-height: 1.2;
}

.meta {
  display: flex;
  gap: 0.75rem;
  align-items: center;
  color: var(--muted);
  font-size: 0.9rem;
  margin-bottom: 1rem;
}

.tags {
  display: flex;
  gap: 0.5rem;
  flex-wrap: wrap;
  margin-bottom: 1rem;
}

.tag {
  background: var(--surface);
  color: var(--accent);
  padding: 0.25rem 0.75rem;
  border-radius: 12px;
  font-size: 0.85rem;
  font-weight: 500;
}

.resources {
  padding: 1rem;
  background: var(--surface);
  border-left: 3px solid var(--accent);
  border-radius: 4px;
  font-size: 0.95rem;
}

.resources strong {
  color: var(--text);
}

.resources a {
  color: var(--accent);
  text-decoration: none;
  border-bottom: 1px solid transparent;
  transition: border-color 0.2s;
}

.resources a:hover {
  border-bottom-color: var(--accent);
}

/* Lead */
.lead {
  margin-bottom: 3rem;
}

.leadQuote {
  font-size: 1.5rem;
  font-weight: 600;
  font-style: italic;
  color: var(--accent-2);
  margin-bottom: 1.5rem;
  line-height: 1.4;
}

.lead p {
  font-size: 1.1rem;
  line-height: 1.7;
  margin-bottom: 1rem;
  color: var(--text);
}

.callout {
  background: var(--surface);
  border-left: 4px solid var(--accent);
  padding: 1rem 1.5rem;
  margin: 2rem 0;
  font-size: 1.1rem;
  line-height: 1.6;
}

.callout strong {
  color: var(--accent);
}

/* Big Picture */
.bigPicture {
  margin-bottom: 4rem;
}

.bigPicture h2 {
  font-size: 2rem;
  margin-bottom: 2rem;
  color: var(--text);
}

.phaseDiagram {
  display: flex;
  align-items: stretch;
  gap: 1rem;
  margin-bottom: 2rem;
  overflow-x: auto;
  padding: 1rem 0;
}

.phaseContainer {
  display: flex;
  align-items: center;
  gap: 1rem;
}

.phase {
  background: var(--surface);
  border: 2px solid var(--surface);
  border-radius: 12px;
  padding: 1.5rem;
  min-width: 220px;
  cursor: pointer;
  transition: all 0.3s;
}

.phase:hover {
  border-color: var(--accent);
  transform: translateY(-2px);
}

.phaseExpanded {
  border-color: var(--accent);
  background: rgba(30, 229, 184, 0.05);
}

.phaseHeader {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  margin-bottom: 1rem;
}

.phaseNumber {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 32px;
  height: 32px;
  background: var(--accent);
  color: var(--bg);
  border-radius: 50%;
  font-weight: 700;
  font-size: 1.1rem;
}

.phaseHeader h3 {
  font-size: 1.2rem;
  font-weight: 700;
  margin: 0;
  flex: 1;
}

.phaseDuration {
  font-size: 0.85rem;
  color: var(--muted);
  white-space: nowrap;
}

.phaseOwner {
  margin-bottom: 0.75rem;
}

.ownerInfo {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-bottom: 0.5rem;
  font-size: 0.9rem;
}

.ownerLabel {
  color: var(--muted);
}

.ownerName {
  color: var(--text);
  font-weight: 600;
}

.aiSupportInfo {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 0.9rem;
}

.avatar {
  border-radius: 50%;
  border: 2px solid var(--accent);
}

.aiSupportName {
  color: var(--accent);
  font-weight: 500;
}

.phaseDescription {
  font-size: 0.9rem;
  color: var(--muted);
  margin: 0;
  line-height: 1.5;
}

.phaseDetails {
  margin-top: 1rem;
  padding-top: 1rem;
  border-top: 1px solid var(--surface);
}

.detailsLink {
  color: var(--accent);
  text-decoration: none;
  font-size: 0.9rem;
  font-weight: 500;
  transition: opacity 0.2s;
}

.detailsLink:hover {
  opacity: 0.7;
}

.arrow {
  font-size: 2rem;
  color: var(--muted);
}

.insight {
  font-size: 1rem;
  color: var(--muted);
  font-style: italic;
  margin-top: 1rem;
}

.insight strong {
  color: var(--text);
}

/* Phase Sections */
.phaseSection {
  margin-bottom: 4rem;
  padding-top: 2rem;
  border-top: 1px solid var(--surface);
}

.phaseSectionHeader {
  margin-bottom: 2rem;
}

.phaseSectionHeader h2 {
  font-size: 1.8rem;
  margin-bottom: 0.75rem;
}

.phaseSectionMeta {
  display: flex;
  gap: 1.5rem;
  font-size: 0.95rem;
  color: var(--muted);
}

.phaseSectionMeta strong {
  color: var(--text);
}

/* Flow Steps */
.flowSteps {
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
  margin-bottom: 2rem;
}

.step {
  display: flex;
  gap: 1rem;
  align-items: flex-start;
}

.stepNumber {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 36px;
  height: 36px;
  min-width: 36px;
  background: var(--accent);
  color: var(--bg);
  border-radius: 50%;
  font-weight: 700;
  font-size: 1rem;
}

.stepContent {
  flex: 1;
}

.stepContent h4 {
  font-size: 1.1rem;
  margin-bottom: 0.75rem;
  color: var(--text);
}

.stepContent ol,
.stepContent ul {
  margin: 0.5rem 0;
  padding-left: 1.5rem;
  line-height: 1.7;
  color: var(--text);
}

.stepContent li {
  margin-bottom: 0.5rem;
}

.stepContent code {
  background: var(--surface);
  padding: 0.2rem 0.5rem;
  border-radius: 4px;
  font-family: 'JetBrains Mono', monospace;
  font-size: 0.9rem;
  color: var(--accent);
}

.checklist {
  list-style: none !important;
  padding-left: 0 !important;
}

.checklist li {
  position: relative;
  padding-left: 1.5rem;
}

/* Callout Boxes */
.calloutBox {
  background: var(--surface);
  border: 1px solid var(--accent);
  border-radius: 8px;
  padding: 1.5rem;
  margin: 2rem 0;
}

.calloutBox h4 {
  font-size: 1.1rem;
  margin-bottom: 0.75rem;
  color: var(--accent);
}

.calloutBox p {
  margin: 0;
  line-height: 1.6;
}

.calloutBox ul {
  margin: 0.5rem 0;
  padding-left: 1.5rem;
  line-height: 1.7;
}

.calloutBox li {
  margin-bottom: 0.5rem;
}

/* Escalation Box */
.escalationBox {
  background: rgba(255, 93, 93, 0.1);
  border-left: 4px solid var(--danger);
  border-radius: 8px;
  padding: 1.5rem;
  margin: 2rem 0;
}

.escalationBox h4 {
  font-size: 1.1rem;
  margin-bottom: 0.75rem;
  color: var(--danger);
}

.escalationBox ul,
.escalationBox ol {
  margin: 0.5rem 0;
  padding-left: 1.5rem;
  line-height: 1.7;
}

.escalationBox li {
  margin-bottom: 0.5rem;
}

.escalationBox strong {
  font-weight: 600;
}

.escalationBox code {
  background: var(--surface);
  padding: 0.2rem 0.5rem;
  border-radius: 4px;
  font-family: 'JetBrains Mono', monospace;
  font-size: 0.9rem;
}

/* Placeholder */
.placeholder {
  background: rgba(255, 200, 87, 0.1);
  border-left: 4px solid var(--warning);
  border-radius: 8px;
  padding: 1.5rem;
  margin: 2rem 0;
}

.placeholder p {
  margin-bottom: 0.75rem;
  line-height: 1.6;
}

.placeholder strong {
  color: var(--warning);
}

.placeholder ol {
  margin: 0.5rem 0;
  padding-left: 1.5rem;
  line-height: 1.7;
}

.placeholder li {
  margin-bottom: 0.5rem;
}

/* Code Block */
.codeBlock {
  background: var(--surface);
  border: 1px solid var(--muted);
  border-radius: 8px;
  padding: 1rem;
  margin: 1rem 0;
  overflow-x: auto;
}

.codeBlock code {
  font-family: 'JetBrains Mono', monospace;
  font-size: 0.9rem;
  color: var(--accent);
  white-space: pre;
}

/* SYNC Example */
.syncExample {
  background: var(--surface);
  border: 1px solid var(--accent);
  border-radius: 8px;
  padding: 1rem;
  margin: 1rem 0;
  overflow-x: auto;
}

.syncExample pre {
  font-family: 'JetBrains Mono', monospace;
  font-size: 0.85rem;
  color: var(--text);
  margin: 0;
  white-space: pre;
  line-height: 1.6;
}

/* Bug Fix Loop */
.bugFixLoop {
  background: var(--surface);
  border-radius: 12px;
  padding: 2rem;
  margin: 2rem 0;
}

.bugFixLoop h3 {
  font-size: 1.3rem;
  margin-bottom: 1.5rem;
  text-align: center;
  color: var(--text);
}

.flowChart {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1rem;
}

.flowStep {
  background: var(--bg);
  border: 2px solid var(--accent);
  border-radius: 8px;
  padding: 0.75rem 1.5rem;
  text-align: center;
  font-weight: 500;
}

.flowArrow {
  font-size: 1.5rem;
  color: var(--accent);
}

.flowSplit {
  display: flex;
  gap: 2rem;
  width: 100%;
  justify-content: center;
}

.flowBranch {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.5rem;
  flex: 1;
  max-width: 200px;
}

.flowArrowLoop {
  font-size: 1.2rem;
  color: var(--danger);
}

.flowArrowContinue {
  font-size: 1.2rem;
  color: var(--success);
}

/* Decision Points */
.decisionPoints {
  margin-bottom: 4rem;
  padding-top: 2rem;
  border-top: 1px solid var(--surface);
}

.decisionPoints h2 {
  font-size: 1.8rem;
  margin-bottom: 2rem;
}

.referenceTable {
  width: 100%;
  border-collapse: collapse;
  margin: 2rem 0;
  background: var(--surface);
  border-radius: 8px;
  overflow: hidden;
}

.referenceTable thead {
  background: var(--bg);
}

.referenceTable th {
  padding: 1rem;
  text-align: left;
  font-weight: 600;
  color: var(--accent);
  border-bottom: 2px solid var(--accent);
}

.referenceTable td {
  padding: 1rem;
  border-bottom: 1px solid var(--bg);
  line-height: 1.6;
}

.referenceTable td strong {
  color: var(--accent);
  font-weight: 600;
}

.referenceTable tbody tr:last-child td {
  border-bottom: none;
}

.referenceTable tbody tr:hover {
  background: rgba(30, 229, 184, 0.05);
}

.escalationRule {
  background: var(--surface);
  border: 2px solid var(--accent);
  border-radius: 12px;
  padding: 1.5rem;
  margin-top: 2rem;
}

.escalationRule h3 {
  font-size: 1.2rem;
  margin-bottom: 0.75rem;
  color: var(--accent);
}

.escalationRule p {
  margin: 0.5rem 0;
  font-size: 1.05rem;
  line-height: 1.6;
}

/* Quiz */
.quiz {
  margin-bottom: 4rem;
  padding-top: 2rem;
  border-top: 1px solid var(--surface);
}

.quiz h2 {
  font-size: 1.8rem;
  margin-bottom: 2rem;
}

.quizQuestion {
  background: var(--surface);
  border-radius: 12px;
  padding: 1.5rem;
  margin-bottom: 1.5rem;
}

.quizQuestion h4 {
  font-size: 1.1rem;
  margin-bottom: 1rem;
  line-height: 1.5;
  color: var(--text);
}

.quizButton {
  background: var(--accent);
  color: var(--bg);
  border: none;
  border-radius: 8px;
  padding: 0.75rem 1.5rem;
  font-size: 0.95rem;
  font-weight: 600;
  cursor: pointer;
  transition: opacity 0.2s;
}

.quizButton:hover {
  opacity: 0.8;
}

.quizAnswer {
  margin-top: 1.5rem;
  padding-top: 1.5rem;
  border-top: 1px solid var(--bg);
}

.quizAnswer p {
  margin-bottom: 0.75rem;
  line-height: 1.6;
}

.quizAnswer ol {
  margin: 0.5rem 0 1rem 0;
  padding-left: 1.5rem;
  line-height: 1.7;
}

.quizAnswer li {
  margin-bottom: 0.5rem;
}

/* Quick Reference */
.quickRef {
  margin-bottom: 4rem;
  padding-top: 2rem;
  border-top: 1px solid var(--surface);
}

.quickRef h2 {
  font-size: 1.8rem;
  margin-bottom: 2rem;
}

.refCard {
  background: var(--surface);
  border: 2px solid var(--accent);
  border-radius: 12px;
  padding: 2rem;
}

.refCard h3 {
  font-size: 1.2rem;
  margin-bottom: 1rem;
  color: var(--accent);
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.refCard ul {
  list-style: none;
  padding: 0;
  margin: 0 0 2rem 0;
}

.refCard li {
  padding: 0.5rem 0;
  line-height: 1.6;
  font-size: 0.95rem;
}

.refCard li strong {
  color: var(--accent);
  font-weight: 600;
}

.refCard p {
  margin: 0.5rem 0;
  font-size: 1rem;
  line-height: 1.6;
}

.refCard h3:last-of-type {
  margin-top: 1.5rem;
  padding-top: 1.5rem;
  border-top: 1px solid var(--bg);
}

/* Next Steps */
.nextSteps {
  margin-bottom: 3rem;
  padding-top: 2rem;
  border-top: 1px solid var(--surface);
}

.nextSteps h2 {
  font-size: 1.8rem;
  margin-bottom: 1rem;
}

.nextSteps p {
  margin-bottom: 1rem;
  line-height: 1.7;
  font-size: 1.05rem;
}

.nextSteps ol {
  margin: 1rem 0 1.5rem 0;
  padding-left: 1.5rem;
  line-height: 1.8;
}

.nextSteps li {
  margin-bottom: 0.75rem;
  font-size: 1.05rem;
}

.nextSteps li strong {
  color: var(--accent);
}

/* Footer */
.footer {
  margin-top: 4rem;
  padding-top: 2rem;
  border-top: 1px solid var(--surface);
  text-align: center;
  color: var(--muted);
  font-size: 0.9rem;
}

.footer p {
  margin: 0.5rem 0;
}

.footer a {
  color: var(--accent);
  text-decoration: none;
  border-bottom: 1px solid transparent;
  transition: border-color 0.2s;
}

.footer a:hover {
  border-bottom-color: var(--accent);
}

/* Responsive */
@media (max-width: 768px) {
  .missionFlow {
    padding: 2rem 1rem;
  }

  .header h1 {
    font-size: 2rem;
  }

  .phaseDiagram {
    flex-direction: column;
  }

  .arrow {
    transform: rotate(90deg);
    margin: 0.5rem 0;
  }

  .flowSplit {
    flex-direction: column;
    gap: 1rem;
  }

  .flowBranch {
    max-width: 100%;
  }

  .referenceTable {
    font-size: 0.9rem;
  }

  .referenceTable th,
  .referenceTable td {
    padding: 0.75rem 0.5rem;
  }
}

