     1→#!/usr/bin/env node
     2→import { promises as fs } from 'node:fs';
     3→import path from 'node:path';
     4→import { fileURLToPath, pathToFileURL } from 'node:url';
     5→import process from 'node:process';
     6→import { execFile } from 'node:child_process';
     7→import { promisify } from 'node:util';
     8→
     9→import { escapeHtml, extractFirstUrl, markdownToHtml } from './utils/markdown.js';
    10→import { emitEvent } from '../scripts/emit.js';
    11→
    12→const execFileAsync = promisify(execFile);
    13→
    14→const __filename = fileURLToPath(import.meta.url);
    15→const __dirname = path.dirname(__filename);
    16→const rootDir = path.resolve(__dirname, '..');
    17→const templatesDir = path.join(__dirname, 'templates');
    18→const tokensPath = path.join(rootDir, 'app', 'styles', 'tokens.css');
    19→
    20→const TAG_PATTERN = /^(evidence-sprint|ac-green)_([a-z0-9-]+)_(\d{4}-\d{2}-\d{2})$/i;
    21→const TYPE_LABEL = {
    22→  'evidence-sprint': 'Evidence sprint',
    23→  'ac-green': 'AC green'
    24→};
    25→const BADGE_ICON = {
    26→  'evidence-sprint': '●',
    27→  'ac-green': '●'
    28→};
    29→
    30→function parseArgs(argv) {
    31→  const options = { out: path.join('public', 'proof'), limit: null, json: null };
    32→  for (let i = 0; i < argv.length; i += 1) {
    33→    const arg = argv[i];
    34→    if (arg === '--out' && argv[i + 1]) {
    35→      options.out = argv[++i];
    36→    } else if (arg === '--limit' && argv[i + 1]) {
    37→      options.limit = Number.parseInt(argv[++i], 10);
    38→    } else if (arg === '--json') {
    39→      options.json = true;
    40→    } else if (arg === '--no-json') {
    41→      options.json = false;
    42→    }
    43→  }
    44→  return options;
    45→}
    46→
    47→async function git(args) {
    48→  try {
    49→    const { stdout } = await execFileAsync('git', args, { cwd: rootDir, encoding: 'utf8' });
    50→    return stdout.trim();
    51→  } catch (error) {
    52→    throw new Error(`Git command failed: git ${args.join(' ')}\n${error.stderr || error.message}`);
    53→  }
    54→}
    55→
    56→function parseTagName(tag) {
    57→  const match = tag.match(TAG_PATTERN);
    58→  if (!match) {
    59→    return null;
    60→  }
    61→  const [, typeRaw, featureRaw, date] = match;
    62→  const type = typeRaw.toLowerCase();
    63→  const feature = featureRaw.toLowerCase();
    64→  return { tag, type, feature, date, sortKey: date, featureLabel: featureRaw };
    65→}
    66→
    67→async function readProofFile(tag, file) {
    68→  try {
    69→    const content = await git(['show', `${tag}:${path.posix.join('proof', file)}`]);
    70→    return content;
    71→  } catch (error) {
    72→    const message = String(error.stderr || error.message || '');
    73→    if (
    74→      /exists on disk, but not in/.test(message) ||
    75→      /fatal: Path /.test(message) ||
    76→      /does not have these files/.test(message) ||
    77→      /fatal: invalid object name/.test(message) ||
    78→      /fatal: bad revision/.test(message)
    79→    ) {
    80→      return null;
    81→    }
    82→    throw error;
    83→  }
    84→}
    85→
    86→async function resolveTagEntry(tagInfo) {
    87→  const { tag, type, feature, date } = tagInfo;
    88→  const ac = await readProofFile(tag, 'AC.md');
    89→  const demo = await readProofFile(tag, 'DEMO.md');
    90→  const delta = await readProofFile(tag, 'DELTA.md');
    91→
    92→  const commit = await git(['rev-parse', '--short', tag]);
    93→
    94→  const acHtml = ac ? markdownToHtml(ac) : '';
    95→  const demoHtml = demo ? markdownToHtml(demo) : '';
    96→  const deltaHtml = delta ? markdownToHtml(delta) : '';
    97→  const demoUrl = demo ? extractFirstUrl(demo) : null;
    98→
    99→  const missingFiles = [];
   100→  if (!ac) missingFiles.push('AC.md');
   101→  if (!demo) missingFiles.push('DEMO.md');
   102→  if (!delta) missingFiles.push('DELTA.md');
   103→
   104→  return {
   105→    tag,
   106→    type,
   107→    feature,
   108→    featureLabel: tagInfo.featureLabel,
   109→    date,
   110→    commit,
   111→    ac: { markdown: ac, html: acHtml, missing: !ac },
   112→    demo: { markdown: demo, html: demoHtml, missing: !demo, url: demoUrl },
   113→    delta: { markdown: delta, html: deltaHtml, missing: !delta },
   114→    missingFiles
   115→  };
   116→}
   117→
   118→function renderTemplate(template, data) {
   119→  return template.replace(/\{\{(\w+)\}\}/g, (_match, key) => {
   120→    return data[key] ?? '';
   121→  });
   122→}
   123→
   124→function buildIndexContent(entries, totalEntries) {
   125→  if (entries.length === 0) {
   126→    return `
   127→      <section class="empty">
   128→        <div class="card">
   129→          <h2>No proof tags yet</h2>
   130→          <p class="muted">Add git tags named <code>evidence-sprint_*</code> or <code>ac-green_*</code> with proof markdown to populate this log.</p>
   131→        </div>
   132→      </section>
   133→    `;
   134→  }
   135→
   136→  const items = entries
   137→    .map((entry) => {
   138→      const dotClass = entry.type === 'ac-green' ? 'dot-green' : 'dot-evidence';
   139→      const displayFeature = escapeHtml(entry.featureLabel || entry.feature);
   140→      const dateLabel = escapeHtml(entry.date);
   141→      return `
   142→        <li data-type="${entry.type}" data-feature="${entry.feature}">
   143→          <span class="dot ${dotClass}" aria-hidden="true"></span>
   144→          <div class="entry">
   145→            <a href="./${entry.tag}/index.html">${escapeHtml(entry.tag)}</a>
   146→            <small class="muted">${dateLabel} • ${displayFeature}</small>
   147→          </div>
   148→        </li>
   149→      `;
   150→    })
   151→    .join('\n');
   152→
   153→  return `
   154→    <section>
   155→      <header class="list-head">
   156→        <h2>Latest ${entries.length} of ${totalEntries}</h2>
   157→      </header>
   158→      <ul class="proof-list">
   159→        ${items}
   160→      </ul>
   161→    </section>
   162→  `;
   163→}
   164→
   165→function buildFilterScript() {
   166→  return `(() => {
   167→    const list = document.querySelectorAll('.proof-list li');
   168→    const typeSelect = document.getElementById('typeFilter');
   169→    const featureInput = document.getElementById('featureFilter');
   170→    const emptyState = document.createElement('p');
   171→    emptyState.className = 'muted empty-msg';
   172→    emptyState.textContent = 'No entries match the current filters.';
   173→
   174→    const parent = document.querySelector('.proof-list');
   175→
   176→    function ensureEmptyState(visible) {
   177→      if (!parent) return;
   178→      const existing = parent.parentElement?.querySelector('.empty-msg');
   179→      if (!visible) {
   180→        if (!existing) {
   181→          parent.parentElement?.appendChild(emptyState);
   182→        }
   183→      } else if (existing) {
   184→        existing.remove();
   185→      }
   186→    }
   187→
   188→    function applyFilters() {
   189→      let visibleCount = 0;
   190→      const typeValue = typeSelect?.value ?? 'all';
   191→      const featureValue = (featureInput?.value ?? '').toLowerCase();
   192→      list.forEach((item) => {
   193→        const matchesType = typeValue === 'all' || item.dataset.type === typeValue;
   194→        const matchesFeature = !featureValue || item.dataset.feature.includes(featureValue);
   195→        const show = matchesType && matchesFeature;
   196→        item.style.display = show ? '' : 'none';
   197→        if (show) visibleCount += 1;
   198→      });
   199→      ensureEmptyState(visibleCount > 0);
   200→    }
   201→
   202→    typeSelect?.addEventListener('change', applyFilters);
   203→    featureInput?.addEventListener('input', applyFilters);
   204→  })();`;
   205→}
   206→
   207→function buildMissingChips(missingFiles) {
   208→  if (!missingFiles.length) {
   209→    return '';
   210→  }
   211→  const chips = missingFiles
   212→    .map((file) => `<span class="chip chip-missing">Missing ${escapeHtml(file)}</span>`)
   213→    .join('\n');
   214→  return `<div class="chip-row">${chips}</div>`;
   215→}
   216→
   217→function ensureBody(content, fallback) {
   218→  if (content && content.trim()) {
   219→    return content;
   220→  }
   221→  return `<p class="muted">${escapeHtml(fallback)}</p>`;
   222→}
   223→
   224→async function writeDetailPage(outDir, entry) {
   225→  const template = await fs.readFile(path.join(templatesDir, 'detail.html'), 'utf8');
   226→  const badgeLabel = `${TYPE_LABEL[entry.type]} · ${entry.tag}`;
   227→  const data = {
   228→    pageTitle: `${entry.tag} • Proof`,
   229→    stylesheet: '../proof.css',
   230→    type: entry.type,
   231→    badgeIcon: BADGE_ICON[entry.type],
   232→    badgeLabel: escapeHtml(badgeLabel),
   233→    typeLabel: TYPE_LABEL[entry.type],
   234→    date: escapeHtml(entry.date),
   235→    feature: escapeHtml(entry.featureLabel || entry.feature),
   236→    demoCta: entry.demo.url
   237→      ? `<p><a class="cta" href="${escapeHtml(entry.demo.url)}" target="_blank" rel="noopener noreferrer">Watch 90s demo →</a></p>`
   238→      : '',
   239→    missingChips: buildMissingChips(entry.missingFiles),
   240→    acBody: ensureBody(entry.ac.html, 'AC.md was not provided in this tag.'),
   241→    demoBody: ensureBody(entry.demo.html, 'DEMO.md was not provided in this tag.'),
   242→    deltaBody: ensureBody(entry.delta.html, 'DELTA.md was not provided in this tag.'),
   243→    tag: escapeHtml(entry.tag),
   244→    commit: escapeHtml(entry.commit)
   245→  };
   246→
   247→  const html = renderTemplate(template, data);
   248→  const tagDir = path.join(outDir, entry.tag);
   249→  await fs.mkdir(tagDir, { recursive: true });
   250→  await fs.writeFile(path.join(tagDir, 'index.html'), html, 'utf8');
   251→}
   252→
   253→async function writeIndexPage(outDir, entries) {
   254→  const template = await fs.readFile(path.join(templatesDir, 'index.html'), 'utf8');
   255→  const latest = entries.slice(0, Math.min(entries.length, 10));
   256→  const content = buildIndexContent(latest, entries.length);
   257→  const script = entries.length ? buildFilterScript() : '';
   258→  const html = renderTemplate(template, {
   259→    content,
   260→    script
   261→  });
   262→  await fs.mkdir(outDir, { recursive: true });
   263→  await fs.writeFile(path.join(outDir, 'index.html'), html, 'utf8');
   264→}
   265→
   266→async function writeJson(outDir, entries) {
   267→  const json = entries.map((entry) => ({
   268→    tag: entry.tag,
   269→    type: entry.type,
   270→    date: entry.date,
   271→    feature: entry.feature,
   272→    featureLabel: entry.featureLabel,
   273→    commit: entry.commit,
   274→    demoUrl: entry.demo.url,
   275→    files: {
   276→      AC: entry.ac.missing ? null : `/proof/${entry.tag}/index.html#ac`,
   277→      DEMO: entry.demo.missing ? null : `/proof/${entry.tag}/index.html#demo`,
   278→      DELTA: entry.delta.missing ? null : `/proof/${entry.tag}/index.html#delta`
   279→    }
   280→  }));
   281→  const serialized = `${JSON.stringify(json, null, 2)}\n`;
   282→  await fs.writeFile(path.join(outDir, 'index.json'), serialized, 'utf8');
   283→}
