   305→.badge { display: inline-flex; align-items: center; gap: var(--slk-gap-2); font-weight: 600; font-size: 0.875rem; padding: 6px 12px; border-radius: 999px; }
   306→.badge-evidence-sprint { color: var(--slk-accent-2); background: rgba(100, 168, 255, 0.16); border: 1px solid rgba(100,168,255,0.4); }
   307→.badge-ac-green { color: var(--slk-success); background: rgba(92,226,126,0.14); border: 1px solid rgba(92,226,126,0.35); }
   308→.cta { background: var(--slk-accent-2); color: #0B1020; border-radius: 8px; padding: 12px 16px; display: inline-flex; align-items: center; font-weight: 600; }
   309→.metrics ul { margin: 0; padding-left: 1.25rem; }
   310→.metrics li { margin-bottom: var(--slk-gap-2); }
   311→.chip-row { display: flex; flex-wrap: wrap; gap: var(--slk-gap-2); }
   312→.chip { display: inline-flex; align-items: center; padding: 4px 10px; border-radius: 999px; font-size: 0.75rem; font-weight: 600; }
   313→.chip-missing { background: rgba(255,93,93,0.18); color: #ff9a9a; border: 1px solid rgba(255,93,93,0.4); }
   314→.prose ul { padding-left: 1.25rem; }
   315→.prose pre { background: rgba(21,26,33,0.8); padding: var(--slk-gap-3); border-radius: 8px; overflow-x: auto; }
   316→.footer { margin-top: var(--slk-gap-6); }
   317→.empty .card { text-align: center; }
   318→.list-head { display: flex; justify-content: space-between; align-items: center; margin-bottom: var(--slk-gap-3); }
   319→.empty-msg { margin-top: var(--slk-gap-3); }
   320→@media (max-width: 720px) {
   321→  .proof-list li { flex-direction: column; align-items: flex-start; }
   322→  .filters { flex-direction: column; align-items: flex-start; }
   323→}
   324→`;
   325→
   326→  await fs.mkdir(outDir, { recursive: true });
   327→  await fs.writeFile(path.join(outDir, 'proof.css'), `${tokens}\n${customCss}`, 'utf8');
   328→}
   329→
   330→async function collectEntries(limit = null) {
   331→  const tagListRaw = await git(['tag', '--list', 'evidence-sprint_*', 'ac-green_*']);
   332→  const tags = tagListRaw ? tagListRaw.split('\n').map((tag) => tag.trim()).filter(Boolean) : [];
   333→  const parsed = [];
   334→  const skipped = [];
   335→
   336→  for (const tag of tags) {
   337→    const info = parseTagName(tag);
   338→    if (!info) {
   339→      skipped.push(tag);
   340→      continue;
   341→    }
   342→    parsed.push(info);
   343→  }
   344→
   345→  parsed.sort((a, b) => {
   346→    if (a.sortKey === b.sortKey) {
   347→      return b.tag.localeCompare(a.tag);
   348→    }
   349→    return b.sortKey.localeCompare(a.sortKey);
   350→  });
   351→
   352→  const limited = typeof limit === 'number' && Number.isFinite(limit) && limit > 0
   353→    ? parsed.slice(0, limit)
   354→    : parsed;
   355→
   356→  const entries = [];
   357→  for (const info of limited) {
   358→    const entry = await resolveTagEntry(info);
   359→    entries.push(entry);
   360→  }
   361→
   362→  return { entries, skipped };
   363→}
   364→
   365→export async function generateProofSite(options = {}) {
   366→  const { out: outArg = path.join('public', 'proof'), limit = null, json } = options;
   367→  const outDir = path.isAbsolute(outArg) ? outArg : path.join(rootDir, outArg);
   368→
   369→  const { entries, skipped } = await collectEntries(limit);
   370→
   371→  if (skipped.length) {
   372→    console.warn(`[proofgen] Skipped ${skipped.length} malformed tag(s): ${skipped.join(', ')}`);
   373→  }
   374→
   375→  await writeStyles(outDir);
   376→  await writeIndexPage(outDir, entries);
   377→
   378→  for (const entry of entries) {
   379→    await writeDetailPage(outDir, entry);
   380→  }
   381→
   382→  const shouldWriteJson = json ?? true;
   383→  if (shouldWriteJson) {
   384→    await writeJson(outDir, entries);
   385→  }
   386→
   387→  await emitEvent('site.proof_updated@1.0', {
   388→    count: entries.length,
   389→    last_tag: entries[0]?.tag ?? null,
   390→    ts: new Date().toISOString()
   391→  });
   392→
   393→  console.log(`[proofgen] Generated ${entries.length} entr${entries.length === 1 ? 'y' : 'ies'} in ${path.relative(rootDir, outDir) || '.'}`);
   394→}
   395→
   396→async function runCli() {
   397→  const options = parseArgs(process.argv.slice(2));
   398→  if (options.limit !== null && (!Number.isFinite(options.limit) || options.limit <= 0)) {
   399→    console.warn('[proofgen] Ignoring invalid --limit value');
   400→    options.limit = null;
   401→  }
   402→  try {
   403→    await generateProofSite(options);
   404→  } catch (error) {
   405→    console.error('[proofgen] Failed to generate proof site:', error.message);
   406→    process.exitCode = 1;
   407→  }
   408→}
   409→
   410→const invokedPath = process.argv[1] ? pathToFileURL(process.argv[1]).href : null;
   411→if (invokedPath && invokedPath === import.meta.url) {
   412→  runCli();
   413→}
   414→

