/** @type {import('next').NextConfig} */
const nextConfig = {
  output: 'export',
  trailingSlash: true,
  images: {
    unoptimized: true,
  },
  distDir: 'public',
};

module.exports = nextConfig;

import type { Metadata } from 'next';
import './globals.css';

export const metadata: Metadata = {
  title: 'ScopeLock Delivery — Executable acceptance criteria, price and outcome locked',
  description: 'ScopeLock co-writes AC.md + tests, delivers through tagged milestones (demo ≤90s, delta) and invoices at AC green.',
  openGraph: {
    title: 'ScopeLock Delivery',
    description: 'Executable acceptance criteria. Price and outcome locked.',
    type: 'website',
    url: 'https://scopelock.dev/',
    images: ['/og-scopelock.svg'],
  },
};

export default function RootLayout({
  children,
}: {
  children: React.ReactNode;
}) {
  return (
    <html lang="en">
      <body>
        {children}
        <footer>
          <small>© ScopeLock — membrane-first, event-native. Tagged milestones, locked criteria.</small>
        </footer>
      </body>
    </html>
  );
}

/* Design Tokens */
:root {
  --slk-bg:#0E1116; --slk-surface:#151A21; --slk-text:#E6EAF2; --slk-muted:#9AA3AE;
  --slk-accent:#1EE5B8; --slk-accent-2:#64A8FF;
  --slk-danger:#FF5D5D; --slk-warning:#FFC857; --slk-success:#5CE27E;
  --slk-radius:10px; --slk-gap-1:4px; --slk-gap-2:8px; --slk-gap-3:12px; --slk-gap-4:16px; --slk-gap-5:24px; --slk-gap-6:32px;
}

*, *::before, *::after {
  box-sizing: border-box;
}

body {
  background:var(--slk-bg);
  background-image: radial-gradient(circle at 20% -10%, rgba(100,168,255,0.18), transparent 60%),
    radial-gradient(circle at 80% 0%, rgba(30,229,184,0.18), transparent 55%);
  color:var(--slk-text);
  font-family:"Inter",system-ui,sans-serif;
  margin:0;
  line-height:1.6;
}

a {
  color:var(--slk-accent-2);
  text-decoration:none;
}

a:focus-visible,
button:focus-visible {
  outline:2px solid var(--slk-accent-2);
  outline-offset:4px;
}

main {
  max-width:1040px;
  margin:auto;
  padding:var(--slk-gap-6) var(--slk-gap-5) var(--slk-gap-6);
  display:flex;
  flex-direction:column;
  gap:var(--slk-gap-6);
}

h1 {
  font-size:clamp(32px,4vw,52px);
  line-height:1.05;
  margin-bottom:var(--slk-gap-3);
}

h2 {
  font-size:1.75rem;
  margin:0;
}

h3 {
  font-size:1.25rem;
  margin:0;
}

p.lead {
  color:var(--slk-muted);
  font-size:1.125rem;
}

section {
  display:flex;
  flex-direction:column;
  gap:var(--slk-gap-3);
}

.card{
  background:var(--slk-surface);
  border-radius:var(--slk-radius);
  padding:var(--slk-gap-5);
}

.badge-event{
  background:rgba(30,229,184,0.12);
  border:1px solid rgba(30,229,184,0.45);
  border-radius:999px;
  padding:2px 8px;
  display:inline-flex;
  align-items:center;
  gap:var(--slk-gap-2);
  font-size:0.875rem;
  color:var(--slk-accent);
}

.badge-event::before {
  content:"";
  width:10px;
  height:10px;
  border-radius:50%;
  background:var(--slk-accent);
  box-shadow:0 0 0 0 rgba(30,229,184,0.6);
}

.event-pulse .badge-event::before {
  animation:slkPulse 400ms ease-in-out alternate infinite;
}

@keyframes slkPulse {
  0% {
    box-shadow:0 0 0 0 rgba(30,229,184,0.45);
  }
  60% {
    box-shadow:0 0 0 8px rgba(30,229,184,0.0);
  }
  100% {
    box-shadow:0 0 0 12px rgba(30,229,184,0.0);
  }
}

.cta{
  background:var(--slk-accent-2);
  color:#0B1020;
  border-radius:8px;
  padding:12px 16px;
  display:inline-flex;
  align-items:center;
  gap:var(--slk-gap-2);
  transition:transform .18s ease;
  font-weight:600;
}

.cta:hover,
.cta:focus-visible{
  transform:translateY(-1px);
}

.grid-3{
  display:grid;
  grid-template-columns:repeat(3,1fr);
  gap:var(--slk-gap-5);
}

code {
  font-family:"JetBrains Mono", "Fira Code", ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;
  background:rgba(21,26,33,0.6);
  border-radius:6px;
  padding:2px 6px;
}

/* Hero Section */
header.hero {
  display:flex;
  flex-direction:column;
  gap:var(--slk-gap-4);
  padding-top:var(--slk-gap-6);
}

.hero-ctas {
  display:flex;
  flex-wrap:wrap;
  align-items:center;
  gap:var(--slk-gap-3);
}

.hero-ctas a:nth-child(2) {
  color:var(--slk-text);
  font-weight:600;
}

.hero-ctas a:nth-child(2):hover,
.hero-ctas a:nth-child(2):focus-visible {
  text-decoration:underline;
}

/* Process Steps */
.intro-list ol,
.scope-steps ol {
  margin:0;
  padding-left:1.25rem;
  display:flex;
  flex-direction:column;
  gap:var(--slk-gap-3);
}

.scope-steps code,
.timeline code {
  background:rgba(21,26,33,0.6);
  border-radius:6px;
  padding:2px 6px;
}

/* Quick Grid */
.scope-quick-grid {
  display:grid;
  gap:var(--slk-gap-4);
  grid-template-columns:repeat(3, minmax(0,1fr));
}

.scope-quick-grid article {
  background:rgba(21,26,33,0.65);
  border-radius:var(--slk-radius);
  padding:var(--slk-gap-4);
  border:1px solid rgba(100,168,255,0.15);
}

.scope-quick-grid strong {
  color:var(--slk-accent);
}

.scope-quick-grid p {
  margin:0;
  color:var(--slk-muted);
  font-size:0.95rem;
}

/* Case Studies */
.card-case header {
  display:flex;
  flex-direction:column;
  gap:var(--slk-gap-2);
}

.case-delta {
  font-size:0.95rem;
  color:var(--slk-muted);
}

.case-meta {
  display:flex;
  flex-wrap:wrap;
  gap:var(--slk-gap-2);
  font-size:0.9rem;
}

.case-meta span {
  background:rgba(21,26,33,0.65);
  border-radius:999px;
  padding:2px 10px;
  border:1px solid rgba(100,168,255,0.18);
}

/* Timeline */
.timeline {
  display:grid;
  gap:var(--slk-gap-4);
}

.timeline-item {
  position:relative;
  padding-left:var(--slk-gap-5);
}

.timeline-item::before {
  content:"";
  position:absolute;
  left:12px;
  top:6px;
  width:12px;
  height:12px;
  border-radius:50%;
  background:var(--slk-accent);
  box-shadow:0 0 0 4px rgba(30,229,184,0.15);
}

.timeline-item::after {
  content:"";
  position:absolute;
  left:17px;
  top:24px;
  bottom:calc(-1 * var(--slk-gap-4));
  width:2px;
  background:linear-gradient(180deg, rgba(100,168,255,0.4), transparent);
}

.timeline-item:last-child::after {
  display:none;
}

.timeline-time {
  color:var(--slk-muted);
  font-size:0.85rem;
}

/* Proof List */
.proof-list ul {
  list-style:none;
  margin:0;
  padding:0;
  display:grid;
  gap:var(--slk-gap-3);
}

.proof-list li {
  background:rgba(21,26,33,0.6);
  border:1px solid rgba(30,229,184,0.28);
  border-radius:var(--slk-radius);
  padding:var(--slk-gap-4);
}

.proof-list code {
  font-weight:600;
}

.proof-search {
  display:flex;
  flex-direction:column;
  gap:var(--slk-gap-2);
}

.proof-search label {
  font-size:0.9rem;
  color:var(--slk-muted);
}

.proof-search input {
  background:rgba(21,26,33,0.75);
  border:1px solid rgba(100,168,255,0.28);
  border-radius:var(--slk-radius);
  padding:10px 12px;
  color:var(--slk-text);
  font-size:1rem;
}

.proof-search input:focus-visible {
  outline:none;
  border-color:var(--slk-accent-2);
  box-shadow:0 0 0 2px rgba(100,168,255,0.35);
}

.proof-empty {
  color:var(--slk-muted);
  font-size:0.9rem;
}

.proof-link {
  display:flex;
  justify-content:flex-start;
  gap:var(--slk-gap-2);
  align-items:center;
  color:var(--slk-muted);
  font-size:0.95rem;
}

.proof-link a {
  font-weight:600;
}

/* Contact */
.contact-actions {
  display:flex;
  flex-wrap:wrap;
  gap:var(--slk-gap-3);
  align-items:center;
}

.contact-actions a[href^="mailto:"] {
  font-weight:600;
}

/* Footer */
footer {
  color:var(--slk-muted);
  font-size:0.85rem;
  text-align:center;
  padding-bottom:var(--slk-gap-6);
}

/* Responsive */
@media (max-width:900px){
  .grid-3{ grid-template-columns:1fr; }
}

@media (max-width:720px) {
  main {
    padding:var(--slk-gap-5) var(--slk-gap-4) var(--slk-gap-6);
  }

  .scope-quick-grid {
    grid-template-columns:1fr;
  }

  .hero-ctas {
    flex-direction:column;
    align-items:flex-start;
  }

  .timeline-item {
    padding-left:var(--slk-gap-4);
  }

  .timeline-item::before {
    left:6px;
  }

  .timeline-item::after {
    left:11px;
  }
}

/* Reduced Motion */
@media (prefers-reduced-motion: reduce) {
  *, *::before, *::after {
    animation-duration:0.01ms !important;
    animation-iteration-count:1 !important;
    transition-duration:0.01ms !important;
    scroll-behavior:auto !important;
  }
}

import Link from 'next/link';
import { promises as fs } from 'fs';
import path from 'path';

interface ProofEntry {
  tag: string;
  type: string;
  date: string;
  feature: string;
  featureLabel: string;
  commit: string;
  demoUrl: string | null;
  files: {
    AC: string | null;
    DEMO: string | null;
    DELTA: string | null;
  };
}

async function getProofEntries(): Promise<ProofEntry[]> {
  try {
    const proofIndexPath = path.join(process.cwd(), 'public', 'proof', 'index.json');
    const data = await fs.readFile(proofIndexPath, 'utf-8');
    return JSON.parse(data);
  } catch (error) {
    console.warn('No proof data found, returning empty array');
    return [];
  }
}

export default async function ProofIndexPage() {
  const entries = await getProofEntries();
  const displayEntries = entries.slice(0, 10);

  return (
    <main className="proof-page">
      <header className="stack intro">
        <h1>Proof Log</h1>
        <p className="lead">Evidence sprints and AC green tags rendered at build time.</p>
        <div className="legend" role="note">
          <span className="dot dot-evidence" aria-hidden="true"></span>
          <span>Evidence sprint</span>
          <span className="dot dot-green" aria-hidden="true"></span>
          <span>AC green</span>
        </div>
      </header>

      {entries.length === 0 ? (
        <section className="empty">
          <div className="card">
            <h2>No proof tags yet</h2>
            <p className="muted">
              Add git tags named <code>evidence-sprint_*</code> or <code>ac-green_*</code> with proof markdown to populate this log.
            </p>
          </div>
        </section>
      ) : (
        <section>
          <header className="list-head">
            <h2>Latest {displayEntries.length} of {entries.length}</h2>
          </header>
          <ul className="proof-list" style={{listStyle: 'none', padding: 0, margin: 0, display: 'flex', flexDirection: 'column', gap: 'var(--slk-gap-3)'}}>
            {displayEntries.map((entry) => {
              const dotClass = entry.type === 'ac-green' ? 'dot-green' : 'dot-evidence';
              const displayFeature = entry.featureLabel || entry.feature;

              return (
                <li key={entry.tag} data-type={entry.type} data-feature={entry.feature} style={{display: 'flex', alignItems: 'center', gap: 'var(--slk-gap-3)', background: 'var(--slk-surface)', padding: 'var(--slk-gap-3) var(--slk-gap-4)', borderRadius: 'var(--slk-radius)', border: '1px solid rgba(230,234,242,0.05)'}}>
                  <span className={`dot ${dotClass}`} aria-hidden="true" style={{width: '12px', height: '12px', borderRadius: '50%', display: 'inline-block'}}></span>
                  <div className="entry" style={{display: 'flex', flexDirection: 'column', gap: '4px'}}>
                    <Link href={`/proof/${entry.tag}`} style={{fontWeight: 600}}>{entry.tag}</Link>
                    <small className="muted">{entry.date} • {displayFeature}</small>
                  </div>
                </li>
              );
            })}
          </ul>
        </section>
      )}

      <style jsx>{`
        .proof-page {
          max-width: 960px;
          margin: 0 auto;
          padding: var(--slk-gap-6) var(--slk-gap-5);
        }
        .stack {
          display: flex;
          flex-direction: column;
          gap: var(--slk-gap-3);
          margin-bottom: var(--slk-gap-6);
        }
        .intro {
          margin-bottom: var(--slk-gap-5);
        }
        .legend {
          display: grid;
          grid-template-columns: auto auto;
          gap: var(--slk-gap-2) var(--slk-gap-4);
          align-items: center;
          margin-top: var(--slk-gap-2);
        }
        .dot-evidence {
          background: var(--slk-accent-2);
        }
        .dot-green {
          background: var(--slk-success);
        }
        .list-head {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: var(--slk-gap-3);
        }
        .empty .card {
          text-align: center;
        }
      `}</style>
    </main>
  );
}

import Link from 'next/link';
import { promises as fs } from 'fs';
import path from 'path';
import { notFound } from 'next/navigation';

interface ProofEntry {
  tag: string;
  type: string;
  date: string;
  feature: string;
  featureLabel: string;
  commit: string;
  demoUrl: string | null;
  files: {
    AC: string | null;
    DEMO: string | null;
    DELTA: string | null;
  };
}

async function getProofEntries(): Promise<ProofEntry[]> {
  try {
    const proofIndexPath = path.join(process.cwd(), 'public', 'proof', 'index.json');
    const data = await fs.readFile(proofIndexPath, 'utf-8');
    return JSON.parse(data);
  } catch (error) {
    return [];
  }
}

async function getProofEntry(tag: string): Promise<ProofEntry | null> {
  const entries = await getProofEntries();
  return entries.find(entry => entry.tag === tag) || null;
}

async function readProofHtml(tag: string): Promise<string | null> {
  try {
    const htmlPath = path.join(process.cwd(), 'public', 'proof', tag, 'index.html');
    const html = await fs.readFile(htmlPath, 'utf-8');

    // Extract just the main content (between <main> tags)
    const mainMatch = html.match(/<main[^>]*>([\s\S]*?)<\/main>/);
    if (mainMatch) {
      return mainMatch[1];
    }
    return html;
  } catch (error) {
    return null;
  }
}

export async function generateStaticParams() {
  const entries = await getProofEntries();
  return entries.map((entry) => ({
    tag: entry.tag,
  }));
}

export async function generateMetadata({ params }: { params: { tag: string } }) {
  const entry = await getProofEntry(params.tag);

  if (!entry) {
    return {
      title: 'Proof Not Found',
    };
  }

  const typeLabel = entry.type === 'ac-green' ? 'AC green' : 'Evidence sprint';

  return {
    title: `${entry.tag} • Proof`,
    description: `${typeLabel} • ${entry.date} • ${entry.featureLabel || entry.feature}`,
  };
}

export default async function ProofDetailPage({ params }: { params: { tag: string } }) {
  const entry = await getProofEntry(params.tag);

  if (!entry) {
    notFound();
  }

  const htmlContent = await readProofHtml(params.tag);

  if (!htmlContent) {
    return (
      <main>
        <div className="card">
          <h1>Proof Not Generated</h1>
          <p>The proof for tag <code>{params.tag}</code> has not been generated yet.</p>
          <Link href="/proof">← Back to proof index</Link>
        </div>
      </main>
    );
  }

  return (
    <>
      <div
        dangerouslySetInnerHTML={{ __html: htmlContent }}
        style={{ maxWidth: '960px', margin: '0 auto', padding: 'var(--slk-gap-6) var(--slk-gap-5)' }}
      />
      <style jsx global>{`
        .stack { display: flex; flex-direction: column; gap: var(--slk-gap-3); margin-bottom: var(--slk-gap-6); }
        .header { display: flex; flex-direction: column; gap: var(--slk-gap-3); margin-bottom: var(--slk-gap-5); }
        .badge { display: inline-flex; align-items: center; gap: var(--slk-gap-2); font-weight: 600; font-size: 0.875rem; padding: 6px 12px; border-radius: 999px; }
        .badge-evidence-sprint { color: var(--slk-accent-2); background: rgba(100, 168, 255, 0.16); border: 1px solid rgba(100,168,255,0.4); }
        .badge-ac-green { color: var(--slk-success); background: rgba(92,226,126,0.14); border: 1px solid rgba(92,226,126,0.35); }
        .muted { color: var(--slk-muted); }
        .chip-row { display: flex; flex-wrap: wrap; gap: var(--slk-gap-2); }
        .chip { display: inline-flex; align-items: center; padding: 4px 10px; border-radius: 999px; font-size: 0.75rem; font-weight: 600; }
        .chip-missing { background: rgba(255,93,93,0.18); color: #ff9a9a; border: 1px solid rgba(255,93,93,0.4); }
        .prose ul { padding-left: 1.25rem; }
        .prose pre { background: rgba(21,26,33,0.8); padding: var(--slk-gap-3); border-radius: 8px; overflow-x: auto; }
        .metrics ul { margin: 0; padding-left: 1.25rem; }
        .metrics li { margin-bottom: var(--slk-gap-2); }
        .footer { margin-top: var(--slk-gap-6); }
      `}</style>
    </>
  );
}

