input.content:
/* Container */
.container {
  min-height: 100vh;
  background: linear-gradient(180deg, #0E1116 0%, #151A21 100%);
  color: #E6EAF2;
  padding: 0;
}

/* Hero Section */
.hero {
  padding: 6rem 2rem 4rem;
  text-align: center;
  background: linear-gradient(135deg, rgba(30, 229, 184, 0.1) 0%, rgba(100, 168, 255, 0.1) 100%);
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.heroContent {
  max-width: 1000px;
  margin: 0 auto;
}

.badge {
  display: inline-block;
  padding: 0.5rem 1rem;
  background: rgba(30, 229, 184, 0.1);
  border: 1px solid rgba(30, 229, 184, 0.3);
  border-radius: 20px;
  font-size: 0.875rem;
  font-weight: 600;
  color: #1EE5B8;
  margin-bottom: 2rem;
  letter-spacing: 0.05em;
  text-transform: uppercase;
}

.heroTitle {
  font-size: 3.5rem;
  font-weight: 800;
  line-height: 1.2;
  margin-bottom: 1.5rem;
  letter-spacing: -0.02em;
}

.heroTitle .highlight {
  background: linear-gradient(135deg, #1EE5B8 0%, #64A8FF 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.heroSubtitle {
  font-size: 1.5rem;
  color: #9AA3AE;
  margin-bottom: 3rem;
  font-weight: 400;
}

.heroComparison {
  display: flex;
  gap: 2rem;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;
  margin-top: 3rem;
}

.comparisonCard {
  background: rgba(255, 255, 255, 0.03);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  padding: 2rem;
  flex: 1;
  min-width: 280px;
  max-width: 350px;
  transition: all 0.3s ease;
}

.comparisonCard:hover {
  transform: translateY(-4px);
  border-color: rgba(255, 255, 255, 0.2);
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
}

.comparisonCard.highlight {
  background: linear-gradient(135deg, rgba(30, 229, 184, 0.1) 0%, rgba(100, 168, 255, 0.1) 100%);
  border-color: #1EE5B8;
}

.comparisonLabel {
  font-size: 0.875rem;
  color: #9AA3AE;
  text-transform: uppercase;
  letter-spacing: 0.05em;
  margin-bottom: 0.5rem;
}

.comparisonTime {
  font-size: 2.5rem;
  font-weight: 700;
  color: #E6EAF2;
  margin-bottom: 0.5rem;
}

.comparisonDesc {
  font-size: 1rem;
  color: #9AA3AE;
}

.comparisonArrow {
  font-size: 2rem;
  color: #1EE5B8;
  font-weight: 700;
}

/* Section */
.section {
  padding: 5rem 2rem;
  max-width: 1200px;
  margin: 0 auto;
}

.sectionTitle {
  font-size: 2.5rem;
  font-weight: 700;
  text-align: center;
  margin-bottom: 1rem;
  letter-spacing: -0.02em;
}

.sectionSubtitle {
  font-size: 1.25rem;
  color: #9AA3AE;
  text-align: center;
  margin-bottom: 3rem;
}

/* Work Breakdown */
.workBreakdown {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;
}

.breakdownChart {
  text-align: center;
}

.pieChart {
  width: 100%;
  max-width: 400px;
  height: auto;
  margin: 0 auto;
}

.aiSlice,
.humanSlice {
  transition: all 1s ease;
}

.chartLabel {
  fill: #E6EAF2;
  font-family: 'Inter', sans-serif;
}

.chartBig {
  font-size: 48px;
  font-weight: 700;
}

.chartSmall {
  font-size: 16px;
  fill: #9AA3AE;
}

.chartLegend {
  margin-top: 2rem;
  display: flex;
  gap: 2rem;
  justify-content: center;
}

.legendItem {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 0.875rem;
}

.legendDot {
  width: 12px;
  height: 12px;
  border-radius: 50%;
}

.breakdownStats {
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
}

.statCard {
  background: rgba(255, 255, 255, 0.03);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  padding: 1.5rem;
  transition: all 0.3s ease;
}

.statCard:hover {
  border-color: #1EE5B8;
  background: rgba(30, 229, 184, 0.05);
}

.statNumber {
  font-size: 3rem;
  font-weight: 700;
  color: #1EE5B8;
  margin-bottom: 0.5rem;
  transition: all 0.5s ease;
}

.statLabel {
  font-size: 1rem;
  font-weight: 600;
  margin-bottom: 0.25rem;
}

.statDesc {
  font-size: 0.875rem;
  color: #9AA3AE;
}

/* Citizen Grid */
.citizenGrid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 1.5rem;
}

.citizenCard {
  background: rgba(255, 255, 255, 0.03);
  border: 2px solid rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  padding: 1.5rem;
  cursor: pointer;
  transition: all 0.3s ease;
}

.citizenCard:hover {
  transform: translateY(-4px);
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.3);
}

.citizenCard.active {
  border-width: 2px;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.4);
}

.citizenHeader {
  display: flex;
  align-items: center;
  gap: 1rem;
}

.citizenIcon {
  width: 48px;
  height: 48px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.5rem;
  flex-shrink: 0;
}

.citizenInfo {
  flex: 1;
}

.citizenName {
  font-size: 1.25rem;
  font-weight: 700;
  margin-bottom: 0.25rem;
}

.citizenRole {
  font-size: 0.875rem;
  color: #9AA3AE;
}

.citizenPercent {
  font-size: 1rem;
  font-weight: 700;
  white-space: nowrap;
}

.citizenDetails {
  margin-top: 1.5rem;
  padding-top: 1.5rem;
  border-top: 1px solid rgba(255, 255, 255, 0.1);
  animation: fadeIn 0.3s ease;
}

@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(-10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.citizenWhat {
  margin-bottom: 1rem;
  font-size: 0.875rem;
  line-height: 1.6;
}

.citizenTasks {
  margin: 1rem 0;
  padding-left: 1.25rem;
  font-size: 0.875rem;
  line-height: 1.8;
  color: #9AA3AE;
}

.citizenTasks li {
  margin-bottom: 0.5rem;
}

.citizenYourJob,
.citizenTime {
  font-size: 0.875rem;
  line-height: 1.6;
  margin-top: 1rem;
}

/* Timeline */
.timeline {
  background: rgba(255, 255, 255, 0.02);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  padding: 2rem;
  margin-bottom: 2rem;
}

.timelineTrack {
  display: flex;
  gap: 1rem;
  margin-bottom: 2rem;
  padding-bottom: 1rem;
  overflow-x: auto;
}

.timelineDay {
  flex: 1;
  min-width: 120px;
  text-align: center;
  cursor: pointer;
  padding: 1rem;
  border: 2px solid transparent;
  border-radius: 8px;
  transition: all 0.3s ease;
  position: relative;
}

.timelineDay:hover {
  background: rgba(255, 255, 255, 0.03);
}

.timelineDay.activeDay {
  background: rgba(255, 255, 255, 0.05);
  border-width: 2px;
}

.timelineDot {
  width: 16px;
  height: 16px;
  border-radius: 50%;
  margin: 0 auto 0.5rem;
}

.timelineLabel {
  font-size: 0.75rem;
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 0.05em;
  margin-bottom: 0.25rem;
}

.timelineTime {
  font-size: 0.875rem;
  color: #9AA3AE;
}

.timelineDetails {
  background: rgba(255, 255, 255, 0.03);
  border-radius: 8px;
  padding: 2rem;
}

.timelineHeader {
  margin-bottom: 1.5rem;
}

.timelineDate {
  font-size: 1.5rem;
  font-weight: 700;
  margin-bottom: 0.5rem;
}

.timelinePhase {
  font-size: 1.25rem;
  font-weight: 600;
}

.timelineInfo {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 1rem;
  margin-bottom: 1.5rem;
  padding-bottom: 1.5rem;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.timelineInfoRow {
  font-size: 0.875rem;
  line-height: 1.6;
}

.timelineSteps {
  font-size: 0.875rem;
  line-height: 1.8;
}

.timelineSteps ol {
  margin-top: 1rem;
  padding-left: 1.5rem;
}

.timelineSteps li {
  margin-bottom: 0.75rem;
  color: #9AA3AE;
}

.timelineSummary {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 1.5rem;
  margin-top: 2rem;
}

.summaryCard {
  background: rgba(255, 255, 255, 0.03);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  padding: 1.5rem;
  text-align: center;
}

.summaryLabel {
  font-size: 0.875rem;
  color: #9AA3AE;
  text-transform: uppercase;
  letter-spacing: 0.05em;
  margin-bottom: 0.5rem;
}

.summaryValue {
  font-size: 2rem;
  font-weight: 700;
  color: #1EE5B8;
  margin-bottom: 0.5rem;
}

.summaryBreakdown {
  font-size: 0.75rem;
  color: #9AA3AE;
  line-height: 1.6;
}

/* View Toggle */
.viewToggle {
  display: flex;
  gap: 1rem;
  justify-content: center;
