.comparisonRow:last-child {
  border-bottom: none;
}

.comparisonRow:hover {
  background: rgba(255, 255, 255, 0.03);
}

.comparisonLabel,
.comparisonTraditional,
.comparisonScopelock {
  padding: 1.25rem 1.5rem;
  font-size: 0.875rem;
