}

.switchStep::before {
  content: '•';
  position: absolute;
  left: 0.5rem;
  color: #1EE5B8;
}

.beforeCount,
.afterCount {
  font-size: 1.125rem;
  font-weight: 700;
  padding-top: 1rem;
