  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.faqItem {
  background: rgba(255, 255, 255, 0.03);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  padding: 1.5rem;
  cursor: pointer;
  transition: all 0.3s ease;
}

.faqItem:hover {
  border-color: rgba(30, 229, 184, 0.5);
  background: rgba(255, 255, 255, 0.05);
