  padding: 0;
  margin: 0;
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
}

.ctaChecklist li {
  font-size: 1rem;
  line-height: 1.6;
  color: #9AA3AE;
  padding-left: 1.5rem;
  position: relative;
}

.ctaChecklist li::before {
  content: '✅';
  position: absolute;
  left: 0;
}

.ctaNext {
  text-align: left;
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  padding: 2rem;
}

.ctaLinks {
  display: flex;
  flex-direction: column;
  gap: 1rem;
  margin-top: 1rem;
}

.ctaLink {
  display: inline-block;
  color: #1EE5B8;
  text-decoration: none;
  font-weight: 600;
  font-size: 1.125rem;
  transition: all 0.2s ease;
}

.ctaLink:hover {
  color: #64A8FF;
  transform: translateX(4px);
}

/* Footer */
.footer {
  padding: 4rem 2rem;
  text-align: center;
  border-top: 1px solid rgba(255, 255, 255, 0.1);
  max-width: 800px;
  margin: 0 auto;
  color: #9AA3AE;
  line-height: 1.8;
}

.footer p {
  margin-bottom: 1.5rem;
}

.footerTagline {
  font-size: 1.125rem;
  font-weight: 500;
  color: #E6EAF2;
}

.footerWelcome {
  font-size: 1.5rem;
  font-weight: 700;
  background: linear-gradient(135deg, #1EE5B8 0%, #64A8FF 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

/* Responsive */
@media (max-width: 1024px) {
  .workBreakdown {
    grid-template-columns: 1fr;
    gap: 2rem;
  }

  .beforeAfter {
    grid-template-columns: 1fr;
  }

  .mockupContent {
    grid-template-columns: 1fr;
  }
}

@media (max-width: 768px) {
  .heroTitle {
    font-size: 2.5rem;
  }

  .heroSubtitle {
    font-size: 1.25rem;
  }

  .heroComparison {
    flex-direction: column;
  }

  .comparisonArrow {
    transform: rotate(90deg);
  }

  .sectionTitle {
    font-size: 2rem;
  }

  .citizenGrid {
    grid-template-columns: 1fr;
  }

  .comparisonRow {
    grid-template-columns: 1fr;
  }

  .comparisonLabel,
  .comparisonTraditional,
  .comparisonScopelock {
    border-right: none;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
  }

  .comparisonScopelock {
    border-bottom: none;
  }

  .economicsGrid {
    grid-template-columns: 1fr;
  }

  .roadmap {
    grid-template-columns: 1fr;
  }

  .timelineTrack {
    flex-direction: column;
  }

  .timelineDay {
    min-width: 100%;
  }

  .ctaTitle {
    font-size: 2rem;
  }
}

