          {learningPath.map((section, idx) => (
            <div key={idx} style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
              <div style={{
                padding: '12px 20px',
                background: 'rgba(21, 26, 33, 0.8)',
                border: `2px solid ${section.badgeColor}`,
                borderRadius: '8px',
                fontSize: '0.875rem',
                fontWeight: 600,
                color: section.badgeColor,
                textAlign: 'center',
              }}>
                <div style={{ marginBottom: '4px' }}>{section.badge}</div>
                <div style={{ fontSize: '0.75rem', color: '#9AA3AE' }}>{section.totalTime}</div>
              </div>
              {idx < learningPath.length - 1 && (
                <div style={{
                  fontSize: '1.5rem',
                  color: '#1EE5B8',
                  fontWeight: 700,
                }}>
                  →
                </div>
              )}
            </div>
          ))}
        </div>
      </section>

      {/* Learning Path Sections */}
      <section style={{
        maxWidth: '1200px',
        margin: '0 auto',
        padding: '60px 20px',
      }}>
        {learningPath.map((section, sectionIdx) => (
          <div key={sectionIdx} style={{
            marginBottom: sectionIdx < learningPath.length - 1 ? '80px' : '0',
          }}>
            {/* Section Header */}
            <div style={{
              marginBottom: '40px',
              padding: '32px',
              background: 'rgba(21, 26, 33, 0.6)',
              border: `1px solid ${section.badgeColor}33`,
              borderLeft: `4px solid ${section.badgeColor}`,
              borderRadius: '12px',
            }}>
              <div style={{
                display: 'flex',
                alignItems: 'center',
                gap: '16px',
                marginBottom: '16px',
                flexWrap: 'wrap',
              }}>
                <div style={{
                  padding: '6px 14px',
                  background: `${section.badgeColor}20`,
                  border: `1px solid ${section.badgeColor}`,
                  borderRadius: '6px',
                  fontSize: '0.75rem',
                  fontWeight: 700,
                  color: section.badgeColor,
                  textTransform: 'uppercase',
                  letterSpacing: '0.05em',
                }}>
                  {section.badge}
                </div>
                <div style={{
                  fontSize: '0.875rem',
                  color: '#9AA3AE',
                }}>
                  {section.totalTime} total reading
                </div>
              </div>

              <h2 style={{
                fontSize: '2rem',
                fontWeight: 700,
                color: '#E6EAF2',
                marginBottom: '8px',
              }}>
                {section.section}
              </h2>

              <div style={{
                fontSize: '1rem',
                color: '#64A8FF',
                marginBottom: '20px',
                fontWeight: 500,
              }}>
                {section.subtitle}
              </div>

              <p style={{
                fontSize: '1.125rem',
                color: '#9AA3AE',
                lineHeight: 1.6,
                marginBottom: '20px',
              }}>
                {section.description}
              </p>

              <div style={{
                padding: '16px',
                background: 'rgba(30, 229, 184, 0.05)',
                border: '1px solid rgba(30, 229, 184, 0.2)',
                borderRadius: '8px',
              }}>
                <div style={{
                  fontSize: '0.875rem',
                  color: '#1EE5B8',
                  fontWeight: 600,
                  marginBottom: '4px',
                }}>
                  ✓ Learning Outcome
                </div>
                <div style={{
                  fontSize: '1rem',
                  color: '#E6EAF2',
                  lineHeight: 1.6,
                }}>
                  {section.outcome}
                </div>
              </div>
            </div>

            {/* Resources Grid */}
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))',
              gap: '32px',
            }}>
              {section.resources.map((resource, resourceIdx) => (
                <Link
                  key={resourceIdx}
                  href={resource.href}
                  style={{
                    textDecoration: 'none',
                    color: 'inherit',
                  }}
                >
                  <div style={{
                    background: 'rgba(21, 26, 33, 0.8)',
                    border: '1px solid rgba(154, 163, 174, 0.1)',
                    borderRadius: '12px',
                    padding: '32px',
                    height: '100%',
                    transition: 'all 0.3s ease',
                    cursor: 'pointer',
                    position: 'relative',
                    overflow: 'hidden',
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.borderColor = section.badgeColor;
                    e.currentTarget.style.transform = 'translateY(-4px)';
                    e.currentTarget.style.boxShadow = `0 8px 24px ${section.badgeColor}40`;
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.borderColor = 'rgba(154, 163, 174, 0.1)';
                    e.currentTarget.style.transform = 'translateY(0)';
                    e.currentTarget.style.boxShadow = 'none';
                  }}
                  >
                    {/* Number badge */}
                    <div style={{
                      position: 'absolute',
                      top: '16px',
                      right: '16px',
                      width: '32px',
                      height: '32px',
                      background: `${section.badgeColor}20`,
                      border: `2px solid ${section.badgeColor}`,
                      borderRadius: '50%',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      fontSize: '0.875rem',
                      fontWeight: 700,
                      color: section.badgeColor,
                    }}>
                      {resourceIdx + 1}
                    </div>

                    {/* Icon */}
                    <div style={{
                      fontSize: '3rem',
                      marginBottom: '16px',
                    }}>
                      {resource.icon}
                    </div>

                    {/* Title */}
                    <h3 style={{
                      fontSize: '1.5rem',
                      fontWeight: 700,
                      color: '#E6EAF2',
                      marginBottom: '12px',
                      lineHeight: 1.3,
                    }}>
                      {resource.title}
                    </h3>

                    {/* Description */}
                    <p style={{
                      color: '#9AA3AE',
                      fontSize: '1rem',
                      lineHeight: 1.6,
                      marginBottom: '20px',
                    }}>
                      {resource.description}
                    </p>

                    {/* What you'll learn */}
                    <div style={{
                      marginBottom: '20px',
                      paddingTop: '20px',
                      borderTop: '1px solid rgba(154, 163, 174, 0.1)',
                    }}>
                      <div style={{
                        fontSize: '0.875rem',
                        color: '#64A8FF',
                        fontWeight: 600,
                        marginBottom: '12px',
                      }}>
                        What You'll Learn:
                      </div>
                      <ul style={{
                        listStyle: 'none',
                        padding: 0,
                        margin: 0,
                        display: 'flex',
                        flexDirection: 'column',
                        gap: '8px',
                      }}>
                        {resource.whatYouLearn.map((item, idx) => (
                          <li key={idx} style={{
                            fontSize: '0.875rem',
                            color: '#9AA3AE',
                            paddingLeft: '20px',
                            position: 'relative',
                          }}>
                            <span style={{
                              position: 'absolute',
                              left: '0',
                              color: section.badgeColor,
                            }}>
                              •
                            </span>
                            {item}
                          </li>
                        ))}
                      </ul>
                    </div>

                    {/* Meta info */}
                    <div style={{
                      display: 'flex',
                      gap: '16px',
                      paddingTop: '20px',
                      borderTop: '1px solid rgba(154, 163, 174, 0.1)',
                    }}>
                      <div style={{ flex: 1 }}>
                        <div style={{ fontSize: '0.75rem', color: '#9AA3AE', marginBottom: '4px' }}>
                          Impact
                        </div>
                        <div style={{ fontSize: '0.875rem', fontWeight: 600, color: section.badgeColor }}>
                          {resource.impact}
                        </div>
                      </div>
                      <div style={{ flex: 1 }}>
                        <div style={{ fontSize: '0.75rem', color: '#9AA3AE', marginBottom: '4px' }}>
                          Reading Time
                        </div>
                        <div style={{ fontSize: '0.875rem', fontWeight: 600, color: '#E6EAF2' }}>
                          {resource.timeToRead}
                        </div>
                      </div>
                    </div>

                    {/* Read arrow */}
                    <div style={{
                      marginTop: '20px',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '8px',
                      color: section.badgeColor,
                      fontWeight: 600,
                      fontSize: '0.9375rem',
                    }}>
                      Start Learning
                      <span style={{ transition: 'transform 0.2s ease' }}>→</span>
                    </div>
                  </div>
                </Link>
              ))}
            </div>

            {/* Section connector */}
            {sectionIdx < learningPath.length - 1 && (
              <div style={{
                textAlign: 'center',
                margin: '60px 0 40px',
              }}>
                <div style={{
                  display: 'inline-flex',
                  alignItems: 'center',
                  gap: '12px',
                  padding: '12px 24px',
                  background: 'rgba(21, 26, 33, 0.8)',
                  border: '1px solid rgba(30, 229, 184, 0.3)',
                  borderRadius: '24px',
                }}>
                  <div style={{
                    fontSize: '1.5rem',
                  }}>
                    ↓
                  </div>
                  <div style={{
                    fontSize: '0.875rem',
                    color: '#1EE5B8',
                    fontWeight: 600,
                  }}>
                    Next: {learningPath[sectionIdx + 1].section}
                  </div>
                </div>
              </div>
            )}
          </div>
        ))}
      </section>

      {/* CTA Section */}
      <section style={{
        maxWidth: '1200px',
        margin: '80px auto 0',
        padding: '0 20px',
      }}>
        <div style={{
          padding: '60px 40px',
          background: 'linear-gradient(135deg, rgba(30, 229, 184, 0.1) 0%, rgba(100, 168, 255, 0.1) 100%)',
          border: '1px solid rgba(30, 229, 184, 0.3)',
          borderRadius: '12px',
          textAlign: 'center',
        }}>
          <h2 style={{
            fontSize: '2.5rem',
            fontWeight: 700,
            color: '#E6EAF2',
            marginBottom: '16px',
          }}>
            Ready to Start?
          </h2>
          <p style={{
            fontSize: '1.25rem',
            color: '#9AA3AE',
            maxWidth: '700px',
            margin: '0 auto 40px',
            lineHeight: 1.6,
          }}>
            Begin with Week 1 Critical Path. Complete all 4 resources (47 minutes total reading),
            then move to Quality Foundations. After Week 1, you'll be ready for your first supervised mission.
          </p>

          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
            gap: '24px',
            maxWidth: '900px',
            margin: '0 auto',
          }}>
            <div style={{
              padding: '24px',
              background: 'rgba(255, 255, 255, 0.05)',
              borderRadius: '8px',
              textAlign: 'left',
            }}>
              <div style={{ fontSize: '2rem', marginBottom: '12px' }}>📚</div>
              <div style={{ fontSize: '1.125rem', fontWeight: 600, color: '#E6EAF2', marginBottom: '8px' }}>
                Week 1: Day 1-2
              </div>
              <div style={{ fontSize: '0.875rem', color: '#9AA3AE' }}>
                Read Critical Path (47 min) → Understand mindset, skills, flow, compensation
              </div>
            </div>

            <div style={{
              padding: '24px',
              background: 'rgba(255, 255, 255, 0.05)',
              borderRadius: '8px',
              textAlign: 'left',
            }}>
              <div style={{ fontSize: '2rem', marginBottom: '12px' }}>🛠️</div>
              <div style={{ fontSize: '1.125rem', fontWeight: 600, color: '#E6EAF2', marginBottom: '8px' }}>
                Week 1: Day 3-7
              </div>
              <div style={{ fontSize: '0.875rem', color: '#9AA3AE' }}>
                Read Quality Foundations (30 min) → Complete first supervised mission → Get paid
              </div>
            </div>

            <div style={{
              padding: '24px',
              background: 'rgba(255, 255, 255, 0.05)',
              borderRadius: '8px',
              textAlign: 'left',
            }}>
              <div style={{ fontSize: '2rem', marginBottom: '12px' }}>🚀</div>
              <div style={{ fontSize: '1.125rem', fontWeight: 600, color: '#E6EAF2', marginBottom: '8px' }}>
                Week 2+
              </div>
              <div style={{ fontSize: '0.875rem', color: '#9AA3AE' }}>
                Read Advanced Execution (38 min) → Complete 2 independent missions → You're productive!
              </div>
            </div>
          </div>
        </div>
      </section>
    </main>
  );
}
