   224→async function writeDetailPage(outDir, entry) {
   225→  const template = await fs.readFile(path.join(templatesDir, 'detail.html'), 'utf8');
   226→  const badgeLabel = `${TYPE_LABEL[entry.type]} · ${entry.tag}`;
   227→  const data = {
   228→    pageTitle: `${entry.tag} • Proof`,
   229→    stylesheet: '../proof.css',
   230→    type: entry.type,
   231→    badgeIcon: BADGE_ICON[entry.type],
   232→    badgeLabel: escapeHtml(badgeLabel),
   233→    typeLabel: TYPE_LABEL[entry.type],
   234→    date: escapeHtml(entry.date),
   235→    feature: escapeHtml(entry.featureLabel || entry.feature),
   236→    demoCta: entry.demo.url
   237→      ? `<p><a class="cta" href="${escapeHtml(entry.demo.url)}" target="_blank" rel="noopener noreferrer">Watch 90s demo →</a></p>`
   238→      : '',
   239→    missingChips: buildMissingChips(entry.missingFiles),
   240→    acBody: ensureBody(entry.ac.html, 'AC.md was not provided in this tag.'),
   241→    demoBody: ensureBody(entry.demo.html, 'DEMO.md was not provided in this tag.'),
   242→    deltaBody: ensureBody(entry.delta.html, 'DELTA.md was not provided in this tag.'),
   243→    tag: escapeHtml(entry.tag),
   244→    commit: escapeHtml(entry.commit)
   245→  };
   246→
   247→  const html = renderTemplate(template, data);
   248→  const tagDir = path.join(outDir, entry.tag);
   249→  await fs.mkdir(tagDir, { recursive: true });
   250→  await fs.writeFile(path.join(tagDir, 'index.html'), html, 'utf8');
   251→}
