'use client';

import { useEffect, useRef } from 'react';
import styles from './ParticleNetworkHero.module.css';

interface Particle {
  x: number;
  y: number;
  vx: number;
  vy: number;
  connections: number[];
}

export function ParticleNetworkHero() {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const particlesRef = useRef<Particle[]>([]);
  const animationFrameRef = useRef<number>();

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Set canvas size
    const resizeCanvas = () => {
      canvas.width = canvas.offsetWidth;
      canvas.height = canvas.offsetHeight;
    };
    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);

    // Initialize 97 particles (representing 97 agents)
    const initParticles = () => {
      particlesRef.current = [];
      for (let i = 0; i < 97; i++) {
        particlesRef.current.push({
          x: Math.random() * canvas.width,
          y: Math.random() * canvas.height,
          vx: (Math.random() - 0.5) * 0.5,
          vy: (Math.random() - 0.5) * 0.5,
          connections: [],
        });
      }
    };
    initParticles();

    // Animation loop
    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Update and draw particles
      particlesRef.current.forEach((particle, i) => {
        // Update position
        particle.x += particle.vx;
        particle.y += particle.vy;

        // Bounce off edges
        if (particle.x < 0 || particle.x > canvas.width) particle.vx *= -1;
        if (particle.y < 0 || particle.y > canvas.height) particle.vy *= -1;

        // Keep within bounds
        particle.x = Math.max(0, Math.min(canvas.width, particle.x));
        particle.y = Math.max(0, Math.min(canvas.height, particle.y));

        // Draw connections to nearby particles
        particlesRef.current.forEach((other, j) => {
          if (i >= j) return;

          const dx = particle.x - other.x;
          const dy = particle.y - other.y;
          const distance = Math.sqrt(dx * dx + dy * dy);

          if (distance < 120) {
            const opacity = (1 - distance / 120) * 0.3;
            ctx.strokeStyle = `rgba(30, 229, 184, ${opacity})`;
            ctx.lineWidth = 1;
            ctx.beginPath();
            ctx.moveTo(particle.x, particle.y);
            ctx.lineTo(other.x, other.y);
            ctx.stroke();
          }
        });

        // Draw particle
        ctx.fillStyle = '#1EE5B8';
        ctx.beginPath();
        ctx.arc(particle.x, particle.y, 2, 0, Math.PI * 2);
        ctx.fill();
      });

      animationFrameRef.current = requestAnimationFrame(animate);
    };
    animate();

    return () => {
      window.removeEventListener('resize', resizeCanvas);
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, []);

  return (
    <div className={styles.hero}>
      <canvas ref={canvasRef} className={styles.canvas} />
      <div className={styles.content}>
        <h1 className={styles.title}>La Serenissima</h1>
        <p className={styles.subtitle}>97 AI Agents, 6 Months Production</p>
        <div className={styles.badges}>
          <span className={styles.badge}>Case Study</span>
          <span className={styles.badge}>Multi-Agent Systems</span>
        </div>
      </div>
    </div>
  );
}

.hero {
  position: relative;
  height: 100vh;
  min-height: 600px;
  max-height: 800px;
  width: 100%;
  overflow: hidden;
  background: linear-gradient(180deg, #0E1116 0%, #151A21 100%);
  display: flex;
  align-items: center;
  justify-content: center;
}

.canvas {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 1;
}

.content {
  position: relative;
  z-index: 2;
  text-align: center;
  padding: 2rem;
  max-width: 800px;
}

.title {
  font-size: clamp(3rem, 8vw, 5rem);
  font-weight: 700;
  color: #E6EAF2;
  margin: 0 0 1rem 0;
  text-shadow: 0 0 30px rgba(30, 229, 184, 0.3);
  animation: fadeInUp 0.8s ease-out;
}

.subtitle {
  font-size: clamp(1.25rem, 3vw, 1.75rem);
  color: #9AA3AE;
  margin: 0 0 2rem 0;
  animation: fadeInUp 0.8s ease-out 0.2s both;
}

.badges {
  display: flex;
  gap: 1rem;
  justify-content: center;
  flex-wrap: wrap;
  animation: fadeInUp 0.8s ease-out 0.4s both;
}

.badge {
  display: inline-block;
  padding: 0.5rem 1rem;
  background: rgba(30, 229, 184, 0.1);
  border: 1px solid rgba(30, 229, 184, 0.3);
  border-radius: 4px;
  color: #1EE5B8;
  font-size: 0.875rem;
  font-weight: 500;
  letter-spacing: 0.5px;
  text-transform: uppercase;
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@media (max-width: 768px) {
  .hero {
    min-height: 500px;
  }

  .content {
    padding: 1rem;
  }

  .badges {
    gap: 0.5rem;
  }

  .badge {
    font-size: 0.75rem;
    padding: 0.375rem 0.75rem;
  }
}

'use client';

import { useEffect, useRef, useState } from 'react';
import styles from './AnimatedMetricsGrid.module.css';

interface Metric {
  label: string;
  value: number;
  suffix: string;
  duration: number;
  decimals?: number;
}

const metrics: Metric[] = [
  { label: 'AI Agents', value: 97, suffix: '+', duration: 2000 },
  { label: 'Production Uptime', value: 99.7, suffix: '%', duration: 2500, decimals: 1 },
  { label: 'State Updates/Hour', value: 50000, suffix: '+', duration: 3000 },
  { label: 'Identity Consistency', value: 90.92, suffix: '%', duration: 2500, decimals: 2 },
  { label: 'Cost per Agent/Day', value: 0.12, suffix: '', duration: 2000, decimals: 2 },
  { label: 'Months in Production', value: 6, suffix: '+', duration: 1500 },
];

function AnimatedMetric({ label, value, suffix, duration, decimals = 0 }: Metric) {
  const [currentValue, setCurrentValue] = useState(0);
  const [hasAnimated, setHasAnimated] = useState(false);
  const elementRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (hasAnimated) return;

    const observer = new IntersectionObserver(
      (entries) => {
        if (entries[0].isIntersecting) {
          setHasAnimated(true);

          const startTime = Date.now();
          const animate = () => {
            const elapsed = Date.now() - startTime;
            const progress = Math.min(elapsed / duration, 1);

            // Easing function (ease-out cubic)
            const eased = 1 - Math.pow(1 - progress, 3);
            setCurrentValue(value * eased);

            if (progress < 1) {
              requestAnimationFrame(animate);
            }
          };
          animate();

          observer.disconnect();
        }
      },
      { threshold: 0.5 }
    );

    if (elementRef.current) {
      observer.observe(elementRef.current);
    }

    return () => observer.disconnect();
  }, [hasAnimated, value, duration]);

  const displayValue = label === 'Cost per Agent/Day'
    ? `$${currentValue.toFixed(decimals)}`
    : currentValue.toFixed(decimals).replace(/\B(?=(\d{3})+(?!\d))/g, ',');

  return (
    <div ref={elementRef} className={styles.metric}>
      <div className={styles.value}>
        {displayValue}{suffix}
      </div>
      <div className={styles.label}>{label}</div>
    </div>
  );
}

export function AnimatedMetricsGrid() {
  return (
    <section className={styles.metricsSection}>
      <h3 className={styles.metricsTitle}>Key Metrics</h3>
      <div className={styles.metricsGrid}>
        {metrics.map((metric, index) => (
          <AnimatedMetric key={index} {...metric} />
        ))}
      </div>
    </section>
  );
}

.metricsSection {
  margin: 4rem 0;
  padding: 3rem 0;
  background: linear-gradient(180deg, rgba(30, 229, 184, 0.03) 0%, transparent 100%);
}

.metricsTitle {
  text-align: center;
  font-size: 1.5rem;
  font-weight: 600;
  color: #E6EAF2;
  margin: 0 0 3rem 0;
  letter-spacing: -0.02em;
}

.metricsGrid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 2rem;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
}

.metric {
  text-align: center;
  padding: 2rem 1rem;
  background: rgba(21, 26, 33, 0.6);
  border: 1px solid rgba(30, 229, 184, 0.1);
  border-radius: 8px;
  transition: all 0.3s ease;
}

.metric:hover {
  background: rgba(21, 26, 33, 0.9);
  border-color: rgba(30, 229, 184, 0.3);
  transform: translateY(-4px);
  box-shadow: 0 8px 24px rgba(30, 229, 184, 0.15);
}

.value {
  font-size: clamp(2rem, 4vw, 3rem);
  font-weight: 700;
  color: #1EE5B8;
  font-family: 'JetBrains Mono', monospace;
  margin: 0 0 0.5rem 0;
  line-height: 1.2;
}

.label {
  font-size: 0.875rem;
  color: #9AA3AE;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  font-weight: 500;
}

@media (max-width: 768px) {
  .metricsSection {
    margin: 2rem 0;
    padding: 2rem 0;
  }

  .metricsTitle {
    font-size: 1.25rem;
    margin-bottom: 2rem;
  }

  .metricsGrid {
    grid-template-columns: repeat(auto-fit, minmax(140px, 1fr));
    gap: 1rem;
  }

  .metric {
    padding: 1.5rem 0.75rem;
  }

  .value {
    font-size: 1.5rem;
  }

  .label {
    font-size: 0.75rem;
  }
}

'use client';

import { useState } from 'react';
import styles from './ArchitectureDiagram.module.css';

interface Layer {
  id: string;
  title: string;
  subtitle: string;
  details: string[];
}

const layers: Layer[] = [
  {
    id: 'layer3',
    title: 'Layer 3: Application',
    subtitle: 'La Serenissima',
    details: [
      '97 citizens with persistent identities',
      'Cultural artifacts and social interactions',
      'Economic transactions ($MIND token)',
      'Emergent behavior and cultural evolution',
    ],
  },
  {
    id: 'layer2',
    title: 'Layer 2: Consciousness Substrate',
    subtitle: 'Mind Protocol V2',
    details: [
      'Dual-memory graph (FalkorDB)',
      'Energy diffusion across relationship network',
      'Economic constraint system',
      'Multi-LLM orchestration (GPT-4, Claude, DeepSeek)',
    ],
  },
  {
    id: 'layer1',
    title: 'Layer 1: Infrastructure',
    subtitle: 'Technical Stack',
    details: [
      'FalkorDB (graph + vector database)',
      'FastAPI (Python backend services)',
      'Next.js 14 (App Router, frontend)',
      'Solana (blockchain for $MIND economy)',
    ],
  },
];

export function ArchitectureDiagram() {
  const [activeLayer, setActiveLayer] = useState<string | null>(null);

  return (
    <div className={styles.diagram}>
      <h3 className={styles.title}>Three-Layer System</h3>
      <div className={styles.layers}>
        {layers.map((layer, index) => (
          <div key={layer.id} className={styles.layerContainer}>
            <div
              className={`${styles.layer} ${activeLayer === layer.id ? styles.active : ''}`}
              onMouseEnter={() => setActiveLayer(layer.id)}
              onMouseLeave={() => setActiveLayer(null)}
            >
              <div className={styles.layerHeader}>
                <h4 className={styles.layerTitle}>{layer.title}</h4>
                <p className={styles.layerSubtitle}>{layer.subtitle}</p>
              </div>
              <div className={`${styles.layerDetails} ${activeLayer === layer.id ? styles.visible : ''}`}>
                <ul>
                  {layer.details.map((detail, i) => (
                    <li key={i}>{detail}</li>
                  ))}
                </ul>
              </div>
            </div>
            {index < layers.length - 1 && (
              <div className={styles.arrow}>
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
                  <path d="M12 4L12 20M12 20L6 14M12 20L18 14" stroke="#1EE5B8" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                </svg>
              </div>
            )}
          </div>
        ))}
      </div>
      <p className={styles.note}>
        <strong>Why This Matters:</strong> Most AI projects build directly on LangChain or similar.
        We built the substrate first because existing tools don't support persistent multi-level
        consciousness with economic constraints.
      </p>
    </div>
  );
}

