     5→
     6→interface Metric {
     7→  label: string;
     8→  value: number;
     9→  suffix: string;
    10→  duration: number;
    11→  decimals?: number;
    12→}
    13→
    14→const metrics: Metric[] = [
    15→  { label: 'AI Agents', value: 97, suffix: '+', duration: 2000 },
    16→  { label: 'Production Uptime', value: 99.7, suffix: '%', duration: 2500, decimals: 1 },
    17→  { label: 'State Updates/Hour', value: 50000, suffix: '+', duration: 3000 },
    18→  { label: 'Identity Consistency', value: 90.92, suffix: '%', duration: 2500, decimals: 2 },
    19→  { label: 'Cost per Agent/Day', value: 0.12, suffix: '', duration: 2000, decimals: 2 },
    20→  { label: 'Months in Production', value: 6, suffix: '+', duration: 1500 },
    21→];
    22→
    23→function AnimatedMetric({ label, value, suffix, duration, decimals = 0 }: Metric) {
    24→  const [currentValue, setCurrentValue] = useState(0);
    25→  const [hasAnimated, setHasAnimated] = useState(false);
    26→  const elementRef = useRef<HTMLDivElement>(null);
    27→
    28→  useEffect(() => {
    29→    if (hasAnimated) return;
    30→
    31→    const observer = new IntersectionObserver(
    32→      (entries) => {
    33→        if (entries[0].isIntersecting) {
    34→          setHasAnimated(true);
    35→
    36→          const startTime = Date.now();
    37→          const animate = () => {
    38→            const elapsed = Date.now() - startTime;
    39→            const progress = Math.min(elapsed / duration, 1);
    40→
    41→            // Easing function (ease-out cubic)
    42→            const eased = 1 - Math.pow(1 - progress, 3);
    43→            setCurrentValue(value * eased);
    44→
    45→            if (progress < 1) {
    46→              requestAnimationFrame(animate);
    47→            }
    48→          };
    49→          animate();
    50→
    51→          observer.disconnect();
    52→        }
    53→      },
    54→      { threshold: 0.5 }
    55→    );
    56→
    57→    if (elementRef.current) {
    58→      observer.observe(elementRef.current);
    59→    }
    60→
    61→    return () => observer.disconnect();
    62→  }, [hasAnimated, value, duration]);
    63→
    64→  const displayValue = label === 'Cost per Agent/Day'
    65→    ? `$${currentValue.toFixed(decimals)}`
    66→    : currentValue.toFixed(decimals).replace(/\B(?=(\d{3})+(?!\d))/g, ',');
    67→
    68→  return (
