def parse_message(name: str, message_body: str) -> dict:
    """Extract chunks from message body."""

    # Extract chunks (CHUNK 1, CHUNK 2, etc.)
    chunks = []

    # Split by CHUNK markers
    lines = message_body.split('\n')
    current_chunk = []
    in_chunk = False

    for line in lines:
        if line.startswith('CHUNK '):
            # Save previous chunk
            if current_chunk:
                chunk_text = '\n'.join(current_chunk).strip()
                if chunk_text:
                    chunks.append(chunk_text)
            current_chunk = []
            in_chunk = True
            continue

        if line.startswith('TIMING:'):
            # End of chunks
            if current_chunk:
                chunk_text = '\n'.join(current_chunk).strip()
                if chunk_text:
                    chunks.append(chunk_text)
            break

        if in_chunk and line.strip():
            current_chunk.append(line)

    return {
        'name': name,
        'chunks': chunks
    }
